sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize:function(){
	},
	isDatePast:function(strDate){
		var currentTime = new GlideDateTime();
		var whenNeeded = new GlideDateTime(strDate);
		if(whenNeeded.before(currentTime)){
			return true;
		}
		else{
			return false;
		}
	},
	isDateToday:function(strDate){
		var currentTime = new GlideDateTime();
		var whenNeeded = new GlideDateTime(strDate);
		if(currentTime.getDate() == whenNeeded.getDate()){
			return true;
		}
		else{
			return false;
		}
	},
	type:'NeedItUtils'
};
